package com.iohk.jormungandrwallet;

import org.junit.Test;

import static org.junit.Assert.*;

/**
 * Example local unit test, which will execute on the development machine (host).
 *
 * @see <a href="http://d.android.com/tools/testing">Testing documentation</a>
 */
public class ExampleUnitTest {
    @Test
    public void addition_isCorrect() {
        assertEquals(4, 2 + 2);
    }

    @Test
    public void recoverYoroiWallet() {
        Wallet wallet = new Wallet("neck bulb teach illegal soul cry monitor claw amount boring provide village rival draft stone");
        byte[] block0 = hexStringToByteArray("");

        assertEquals(0, wallet.value());

        Settings settings = wallet.initialFunds(block0);

        assertEquals(0, wallet.value());
    }

    private byte[] hexStringToByteArray(String s) {
        int len = s.length();
        byte[] data = new byte[len / 2];
        for (int i = 0; i < len; i += 2) {
            data[i / 2] = (byte) ((Character.digit(s.charAt(i), 16) << 4)
                    + Character.digit(s.charAt(i+1), 16));
        }
        return data;
    }

}